mapper.select:
select min(session_id) session_id
  from nhdplus_navigation.navigation_cache_status
 where return_code = 0 and
       start_comid = #{comid} and
       navigation_mode = #{navigationMode}


        and max_distance = #{distance}


        and max_distance is null




        and stop_comid = #{stopComid}


        and stop_comid is null

mapper.sql:
with recursive nav(comid, terminalpathid, dnhydroseq, pathlength, lengthkm, startlength) as (select comid, terminalpathid, dnhydroseq, pathlength, lengthkm, pathlength + lengthkm startlength from nhdplus.plusflowlinevaa_np21 where comid = #{comid,jdbcType=NUMERIC} union all select x.comid, x.terminalpathid, x.dnhydroseq, x.pathlength, x.lengthkm, nav.startlength from nhdplus.plusflowlinevaa_np21 x, nav where x.hydroseq = nav.dnhydroseq and x.terminalpathid = nav.terminalpathid ), navigation_results as (select * from nav
nav.pathlength + nav.lengthkm >= nav.startlength - #{distance}
)

with recursive initial_nav(comid, dnhydroseq, dnminorhyd, navlength, startNavLength) as (select comid, dnhydroseq, dnminorhyd, lengthkm+0 navlength, 0.0 startNavLength from nhdplus.plusflowlinevaa_np21 where comid = #{comid,jdbcType=NUMERIC} union all select x.comid, x.dnhydroseq, x.dnminorhyd, initial_nav.navlength + x.lengthkm, initial_nav.navlength startNavLength from nhdplus.plusflowlinevaa_np21 x, initial_nav where x.hydroseq = initial_nav.dnhydroseq or x.hydroseq = initial_nav.dnminorhyd ), dup_nav as (select initial_nav.*, row_number() over (partition by comid) rowNumber from initial_nav
initial_nav.startNavLength <= #{distance}
), navigation_results as (select * from dup_nav where rowNumber = 1 )

with recursive nav(comid, levelpathid, uphydroseq, pathlength, startlength) as (select comid, levelpathid, uphydroseq, pathlength, pathlength+0 startlength from nhdplus.plusflowlinevaa_np21 where comid = #{comid,jdbcType=NUMERIC} union all select x.comid, x.levelpathid, x.uphydroseq, x.pathlength, nav.startlength from nhdplus.plusflowlinevaa_np21 x, nav where x.hydroseq = nav.uphydroseq and x.levelpathid = nav.levelpathid ), navigation_results as (select * from nav
nav.pathlength <= nav.startlength + #{distance}
)

with recursive nav(comid, hydroseq, levelpathid, uphydroseq, pathlength, lengthkm, startlength, navlength) as (select comid, hydroseq, levelpathid, uphydroseq, pathlength, lengthkm, pathlength+0 startlength, lengthkm+0 navlength from nhdplus.plusflowlinevaa_np21 where comid = #{comid,jdbcType=NUMERIC} union all select x.comid, x.hydroseq, x.levelpathid, x.uphydroseq, x.pathlength, x.lengthkm, nav.startlength, nav.navlength + x.lengthkm from nhdplus.plusflowlinevaa_np21 x, nav where x.dnhydroseq = nav.hydroseq and x.dnlevelpathid = nav.levelpathid ), navigation_results as (select * from nav
nav.pathlength <= nav.startlength + #{distance}
)
